sysReq.RecordTypeId     = "01280000000Brli";
sysReq.Opportunity__c  = "{!Opportunity.Id}";

var result = sforce.connection.create( [ sysReq ] );

if( result[0].getBoolean( "success" ) ) {
    alert( "A new Systems Request record was created successfully." );

    location.reload();
}
else {
    alert( "An Error has Occurred. Error:" + result[0] );
}
