var Celigo = {};
	Celigo.SFDC = (function() {
	var opportunitySyncManager = function() {
	this.triggerSync = function() {
	this.openPopup();
	};

	this.openPopup = function() {

	var top = screen.height - (screen.height * .7) - 80,
	left = screen.width - (screen.width * .65) - 87,
	params = 'dependent = yes,resizable=yes,scrollbars=false,toolbar=false,menubar=false,location=false,status=true,directories=false,width=575,height=200,top=';

	params += top.toString();
	params += ',left=' + left.toString() + '\'';

	{!REQUIRESCRIPT("/soap/ajax/28.0/connection.js")}

	this.pu = window.open('/apex/netsuite_conn__Order_Sync?whence='+"{!Order.Id}", 'NetSuite_Synchronization', params);
	};
	};

	return {
	main : function() {
	Celigo.opportunitySyncMgr = new opportunitySyncManager();
	Celigo.opportunitySyncMgr.triggerSync();
	}
	};
	})();
	Celigo.SFDC.main();
